pragma solidity ^0.4.20;

contract DonationBlock {

    struct DonationStruct {
        bytes32 dimension;
        uint metric;
        uint index;
    }

    mapping(address => DonationStruct) private DonationStructs;
    address[] private DonationIndex;

    event LogNewDonation(address indexed donationAddress, uint index, bytes32 dimension, uint metric);
    event LogUpdateDonation(address indexed donationAddress, uint index, bytes32 dimension, uint metric);

    function isDonation(address donationAddress)
    public 
    view
    returns(bool isIndeed) 
    {
        if(DonationIndex.length == 0) return false;
        return (DonationIndex[DonationStructs[donationAddress].index] == donationAddress);
    }

    function insertDonation(
        address donationAddress, 
        bytes32 dimension, 
        uint    metric) 
        public
        returns(uint index)
    {
        if(isDonation(donationAddress)) revert(); 
        DonationStructs[donationAddress].dimension = dimension;
        DonationStructs[donationAddress].metric = metric;
        DonationStructs[donationAddress].index = DonationIndex.push(donationAddress)-1;
        emit LogNewDonation(
            donationAddress, 
            DonationStructs[donationAddress].index, 
            dimension, 
            metric);
        return DonationIndex.length-1;
    }

    function getDonation(address donationAddress)
        public 
        view
        returns(bytes32 dimension, uint metric, uint index)
    {
        if(!isDonation(donationAddress)) revert(); 
        return(
            DonationStructs[donationAddress].dimension, 
            DonationStructs[donationAddress].metric, 
            DonationStructs[donationAddress].index);
    } 

    function updateDimension(address donationAddress, bytes32 dimension) 
    public
    returns(bool success) 
    {
        if(!isDonation(donationAddress)) revert(); 
        DonationStructs[donationAddress].dimension = dimension;
        emit LogUpdateDonation(
            donationAddress, 
            DonationStructs[donationAddress].index,
            dimension, 
            DonationStructs[donationAddress].metric);
        return true;
    }

    function updateMetric(address donationAddress, uint metric) 
    public
    returns(bool success) 
    {
        if(!isDonation(donationAddress)) revert(); 
        DonationStructs[donationAddress].metric = DonationStructs[donationAddress].metric + metric;
        emit LogUpdateDonation(
            donationAddress, 
            DonationStructs[donationAddress].index,
            DonationStructs[donationAddress].dimension, 
            metric);
        return true;
    }

    function getUserCount() 
    public
    view
    returns(uint count)
    {
        return DonationIndex.length;
    }

    function getUserAtIndex(uint index)
    public
    view
    returns(address donationAddress)
    {
        return DonationIndex[index];
    }

}
