pragma solidity ^0.4.24;

contract EventBlock {

    struct EventStruct {
        string title;
        string description;
        uint index;
    }

    mapping(address => EventStruct) private EventStructs;
    address[] private EventIndex;

    event LogNewEvent(address indexed eventAddress, uint index, string title, string description);
    event LogUpdateEvent(address indexed eventAddress, uint index, string title, string description);

    function isEvent(address eventAddress)
    public 
    view
    returns(bool isIndeed) 
    {
        if(EventIndex.length == 0) return false;
        return (EventIndex[EventStructs[eventAddress].index] == eventAddress);
    }

    function insertEvent(
        address eventAddress, 
        string title, 
        string description) 
        public
        returns(uint index)
    {
        if(isEvent(eventAddress)) revert(); 
        EventStructs[eventAddress].title = title;
        EventStructs[eventAddress].description = description;
        EventStructs[eventAddress].index = EventIndex.push(eventAddress)-1;
        emit LogNewEvent(
            eventAddress, 
            EventStructs[eventAddress].index, 
            title, 
            description);
        return EventIndex.length-1;
    }

    function getEvent(address eventAddress)
        public 
        view
        returns(string title, string description, uint index)
    {
        if(!isEvent(eventAddress)) revert(); 
        return(
            EventStructs[eventAddress].title, 
            EventStructs[eventAddress].description, 
            EventStructs[eventAddress].index);
    } 

    function updateDimension(address eventAddress, string title) 
    public
    returns(bool success) 
    {
        if(!isEvent(eventAddress)) revert(); 
        EventStructs[eventAddress].title = title;
        emit LogUpdateEvent(
            eventAddress, 
            EventStructs[eventAddress].index,
            title, 
            EventStructs[eventAddress].description);
        return true;
    }

    function updateDescription(address eventAddress, string description) 
    public
    returns(bool success) 
    {
        if(!isEvent(eventAddress)) revert(); 
        EventStructs[eventAddress].description = description;
        emit LogUpdateEvent(
            eventAddress, 
            EventStructs[eventAddress].index,
            EventStructs[eventAddress].title, 
            description);
        return true;
    }

    function getUserCount() 
    public
    view
    returns(uint count)
    {
        return EventIndex.length;
    }

    function getUserAtIndex(uint index)
    public
    view
    returns(address eventAddress)
    {
        return EventIndex[index];
    }

}
